<style>
    .dev .edu,
    .edu .dev {
        display: none;
    }

    div {
        width: 100%;
        height: 100%;
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        display: flex;
        justify-content: space-between;
        align-items: center;
    }
    div::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-image: linear-gradient(
            to bottom,
            #ffffff55,
            10%,
            transparent,
            90%,
            #00000055
        );
        z-index: 15;
    }
    svg {
        height: 60%;
        width: auto;
        z-index: 12;
    }
    .dev * {
        --color: var(--color-opposite);
    }
    .edu * {
        --color: var(--color-primary);
    }
</style>
